# -*- coding: utf-8 -*-from application import dbfrom sqlalchemy.schema import Columnfrom sqlalchemy.types import Integer, String, TIMESTAMPimport datetimeclass Profile(db.Model):    id          = Column(Integer, primary_key=True)    path        = Column(String(255),nullable=False)    ftphost     = Column(String(255),nullable=False)    ftpuser     = Column(String(255),nullable=False)    ftppass     = Column(String(255),nullable=False)    ftppath     = Column(String(255),nullable=False)    created     = Column(TIMESTAMP, nullable=False,server_default='0000-00-00 00:00:00')    updated     = Column(TIMESTAMP, nullable=False,server_default='0000-00-00 00:00:00')    def __init__(self, path, ftphost, ftpuser, ftppass,ftppath):        self.path=path        self.ftphost=ftphost        self.ftpuser=ftpuser        self.ftppass=ftppass        self.ftppath=ftppath        self.created=datetime.datetime.now().strftime("%Y-%m-%d %H:%M:%S")        self.updated=self.created