# -*- coding: utf-8 -*-from application import dbfrom sqlalchemy.schema import Columnfrom sqlalchemy.types import Integer, String, TIMESTAMPfrom sqlalchemy.dialects.mysql import TINYINTimport datetimeclass Projects(db.Model):    id          = Column(Integer, primary_key=True)    name        = Column(String(20),nullable=False, unique=True)    path        = Column(String(255),nullable=False)    giturl      = Column(String(255),nullable=False)    ftphost     = Column(String(255),nullable=False)    ftpuser     = Column(String(255),nullable=False)    ftppass     = Column(String(255),nullable=False)    ftppath     = Column(String(255),nullable=False)    content     = Column(String(255),nullable=False)    status     = Column(TINYINT(1),nullable=False,index=True,server_default='0')    deleted     = Column(TINYINT(1),nullable=False,index=True,server_default='0')    created     = Column(TIMESTAMP, nullable=False,server_default='0000-00-00 00:00:00')    updated     = Column(TIMESTAMP, nullable=False,server_default='0000-00-00 00:00:00')    def __init__(self, name, path, giturl, ftphost, ftpuser, ftppass,ftppath):        self.name=name        self.path=path        self.giturl=giturl if giturl else ''        self.ftphost=ftphost if ftphost else ''        self.ftpuser=ftpuser if ftpuser else ''        self.ftppass=ftppass if ftppass else ''        self.ftppath=ftppath if ftppath else ''        self.content=self.name        self.status=0        self.deleted=0        self.created=datetime.datetime.now().strftime("%Y-%m-%d %H:%M:%S")        self.updated=self.created    @property    def status_desc(self):        if self.status == 0:           return  '入库'        elif self.status == 1:           return  '初始化'        elif self.status == 2:           return  'status'        elif self.type == 3:           return  '可开发'