# -*- coding: utf-8 -*-from gittle import Gittleclass Git():    def __init__(self,repo_url,repo_path):        self.repo_url   = repo_url        self.repo_path  = repo_path        self.__repo_path()    def status(self):        repo=Gittle(self.repo_path)        modfiles=repo.modified_files        addfiles=repo.added_files        remfiles=repo.removed_files        return {"mod":modfiles,"add":addfiles,"rem":remfiles}    def clone(self):        Gittle.clone(self.repo_url, self.repo_path)    def __repo_path(self):        first_pos=self.repo_url.find("/")        last_pos=self.repo_url.find(".git")        repo_name=self.repo_url[first_pos+1:last_pos]        if not self.repo_path.endswith("/"):            self.repo_path="%s/"%self.repo_path        self.repo_path="%s%s"%(self.repo_path, repo_name)